mapper.select:
SELECT ACC_DATE, CATEGORY, CONTENT, AMOUNT, ACC_NO FROM SW_ACCOUNT WHERE ACC_DATE LIKE #{dateVO.year} -- 어떤 year인지 모르기 때문에 dateVO로 지정해주기
|| '-' || #{dateVO.month}
|| '-' || #{dateVO.date}
|| '%' AND ACC_DELETE = FALSE AND mber_id = #{mberVO.mberId} ORDER BY ACC_DATE DESC

mapper.update:
UPDATE sw_account
SET  ACC_DATE = #{accDate}
, CATEGORY = #{category}
, CONTENT = #{content}
, AMOUNT = #{amount}
WHERE acc_no = #{accNo}
AND mber_id = #{mberId}

mapper.select:
update sw_account
set acc_delete = true
where acc_no = #{accNo}
